var now = new Date();

function createtime() {
  var startTime = new Date("04/01/2025 00:00:00");
  now.setTime(now.getTime() + 250);

  var totalSeconds = (now - startTime) / 1000;
  var days = Math.floor(totalSeconds / (60 * 60 * 24));
  var hours = Math.floor((totalSeconds / (60 * 60)) % 24);
  var minutes = Math.floor((totalSeconds / 60) % 60);
  var seconds = Math.round(totalSeconds % 60);

  // 补零处理
  if (hours < 10) hours = "0" + hours;
  if (minutes < 10) minutes = "0" + minutes;
  if (seconds < 10) seconds = "0" + seconds;

  const html = `
    本站已运行：${days} 天 ${hours} 小时 ${minutes} 分 ${seconds} 秒
    <i class='fas fa-heartbeat' style='color:red'></i>
  `;

  const board = document.getElementById("workboard");
  if (board) board.innerHTML = html;
}

setInterval(createtime, 250);
