// 精简版网站运行时间计数器
var now = new Date();
var siteCreationDate = new Date("04/01/2025 00:00:00"); // 修改为你的网站创建时间

function updateRunTime() {
  now.setTime(now.getTime() + 1000); // 每秒更新
  
  // 计算时间差（毫秒）
  var diffInMilliseconds = now - siteCreationDate;
  
  // 转换为秒、分、时、天
  var seconds = Math.floor(diffInMilliseconds / 1000);
  var minutes = Math.floor(seconds / 60);
  var hours = Math.floor(minutes / 60);
  var days = Math.floor(hours / 24);
  
  // 计算剩余时间
  hours = hours % 24;
  minutes = minutes % 60;
  seconds = seconds % 60;
  
  // 格式化显示（补零）
  hours = hours.toString().padStart(2, '0');
  minutes = minutes.toString().padStart(2, '0');
  seconds = seconds.toString().padStart(2, '0');
  
  // 更新显示
  var runtimeElement = document.getElementById("runtime");
  if (runtimeElement) {
    runtimeElement.textContent = `${days}天 ${hours}小时 ${minutes}分 ${seconds}秒`;
  }
}

// 每秒更新一次
setInterval(updateRunTime, 1000);

// 立即执行一次
updateRunTime();